package com.sci;

public class TenaryOpt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		int j = 20;
		String data = i < j ? "This is true" : "This is false";
		System.out.println(data);
		int num = i < j ? 100 : 200;
		System.out.println(num);
		char c = i < j ? 'A' : 'B';
		System.out.println(c);
	}

}
